import React, {useState, useEffect} from 'react';
import './Map.css';

const Map = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [restaurantData, setRestaurantData] = useState(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3000/userInformation')
      .then(response => response.json())
      .then(data => setUserInfo(data))
      .catch(error => console.error(error));
  }, []);

  useEffect(() => {
    fetch('http://localhost:3000/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error(error));
  }, []);

  useEffect(() => {
    const options = {
      zoom:15,
      center: {lat:41.507213,lng:-81.606955},
      streetViewControl: false,
      //Hide Default Google Maps Icons
      styles: [
        {
          featureType: "poi",
          elementType: "labels",
          stylers: [
            { visibility: "off" }
          ]
        },
          {
            featureType: "transit",
            stylers: [
              { visibility: "off" }
            ]
          }
      ]
    }

    const script = document.createElement('script');
    script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyB2zN4XibhG6DPOVT9Xy_smKHsgNEXMwGY";
    script.async = true;
    script.onload = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), options);
      setMap(map);
      /*
      restaurantData.forEach(restaurant => {
        addMarker(
          restaurant.name,
          restaurant.location,
          restaurant.payOptions,
          restaurant.hours,
          restaurant.popItems,
          restaurant.waitTime,
          restaurant.img
        )
      })
      */
    };
    document.body.appendChild(script);
  }, [map, restaurantData]);

  if (userInfo == null || restaurantData == null) {
    return <div>Loading...</div>;
  }

  function addMarker(name, location, payOptions, hours, popItems, waitTime, img) {
    const coords = location;
    img = img.replace(/&amp;/g, "&");
    const [lat, lng] = coords.split(", ");
    const formattedCoords = { lat: Number(lat), lng: Number(lng) };
    location = formattedCoords;


    var marker = new window.google.maps.Marker({
      position: location,
      map:map,
      icon: img
    })

    const contentString = 
    `<div>
      <h1>${name}</h1>
      <p>Payment Options: ${payOptions}</p>
      <p>Hours: ${hours}</p>
      <p>Popular Items: ${popItems}</p>
      <p>Est. Wait Time: ${waitTime}</p>
      <a href="/index.html">
        <button>Check Reviews</button>
      </a>
    </div>`;

    var infoWindow = new window.google.maps.InfoWindow({
      content: contentString
    });

    marker.addListener('click', function() {
        infoWindow.open(map, marker);
    });
  }

  return (
    <div>
      <head>
        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css" />
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width-device-width, initial-scale=1.0" />
        <meta httpEquiv="X-UA-Compatible" content="ie=edge" />
        <title>My Google Maps</title>
      </head>
      <div className="well" id="studentInfo">
        <div id="header">Your Info</div>
        <div>CaseCash: {userInfo.caseCash}</div>
        <div>Portable Swipes: {userInfo.portSwipes}</div>
        <div>Meal Swipes: {userInfo.mealSwipes}</div> 
        <div>Review Points: {userInfo.reviewPoints}</div>
      </div>
      <div id="map"></div>
    </div>
  );
}

export default Map;
