import './Map.css';
import React from 'react';

function initMap() {
    const google = window.google;

    var options = {
        zoom:15,
        center: {lat:41.507213,lng:-81.606955},
        streetViewControl: false,
        //Hide Default Google Maps Icons
        styles: [
            {
                featureType: "poi",
                elementType: "labels",
                stylers: [
                    { visibility: "off" }
                ]
            },
            {
                featureType: "transit",
                stylers: [
                    { visibility: "off" }
                ]
            }
        ]
    }
    //creates the map
    var map = new google.maps.Map(document.getElementById('map'), options);

    function addMarker(name, location, payOptions, hours, popItems, waitTime, img){
        const coords = location;
        img = img.replace(/&amp;/g, "&");
        const [lat, lng] = coords.split(", ");
        const formattedCoords = { lat: Number(lat), lng: Number(lng) };
        location = formattedCoords;


        var marker = new google.maps.Marker({
        position: location,
        map:map,
        icon: img
    })

        const contentString = 
            `<div>
                <h1>${name}</h1>
                <p>Payment Options: ${payOptions}</p>
                <p>Hours: ${hours}</p>
                <p>Popular Items: ${popItems}</p>
                <p>Est. Wait Time: ${waitTime}</p>
                <a href="/index.html">
                    <button>Check Reviews</button>
                </a>
            </div>`;

        var infoWindow = new google.maps.InfoWindow({
            content: contentString
        });

        marker.addListener('click', function() {
            infoWindow.open(map, marker);
        });
    }
    data.forEach(restaurant =>{
        addMarker(
            restaurant.name,
            restaurant.location,
            restaurant.payOptions,
            restaurant.hours,
            restaurant.popItems,
            restaurant.waitTime,
            restaurant.img
        )
    })
}

const Map = () => {
    return (
        <div>
            <head>
                <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css" />
                <meta charset="UTF-8" />
                <meta name="viewport" content="width-device-width, initial-scale=1.0" />
                <meta http-equiv="X-UA-Compatible" content="ie=edge" />
                <title>My Google Maps</title>
            </head>
            <body>
                <div class="well" id="studentInfo">
                    <div id="header">Your Info</div>
                    <div id="casecash">CaseCash:&nbsp;</div>
                    <div id="portswipes">Portable Swipes:&nbsp;</div>
                    <div id="mealswipes">Meal Swipes:&nbsp;</div> 
                    <div id="reviewpoints">Review Points:&nbsp;</div>
                </div>
                <div id="map"></div>
                <div id="data"></div>
            </body>
        </div>
    );
}

export default Map;
