import React, {useState, useEffect} from 'react';
import './Map.css';

window.initMap = function() {}

const Map = () => {
  const [userInfo, setUserInfo] = useState(null);
  const [restaurantData, setRestaurantData] = useState(null);
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    fetch('http://localhost:3000/userInformation')
      .then(response => response.json())
      .then(data => setUserInfo(data))
      .catch(error => console.error(error));
  }, [loaded]);

  useEffect(() => {
    fetch('http://localhost:3000/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error(error));
  }, [loaded]);

  useEffect(() => {
    const options = {
      zoom:15,
      center: {lat:41.507213,lng:-81.606955},
      streetViewControl: false,
      //Hide Default Google Maps Icons
      styles: [
        {
          featureType: "poi",
          elementType: "labels",
          stylers: [
            { visibility: "off" }
          ]
        },
          {
            featureType: "transit",
            stylers: [
              { visibility: "off" }
            ]
          }
      ]
    }

    if (document.getElementById('map') == null) {
      const mapDiv = document.createElement('div');
      mapDiv.id = "map";
      document.body.appendChild(mapDiv);

      const script = document.createElement('script');
      script.type = 'text/javascript';
      script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyB2zN4XibhG6DPOVT9Xy_smKHsgNEXMwGY&callback=initMap";
      script.async = true;
      script.onload = () => {
        new window.google.maps.Map(mapDiv, options);
      };
      document.body.appendChild(script);
    }
  }, [loaded]);

  useEffect(() => {
    function addMarker(name, location, payOptions, hours, popItems, waitTime, img){
      const coords = location;
      img = img.replace(/&amp;/g, "&");
      const [lat, lng] = coords.split(", ");
      const formattedCoords = { lat: Number(lat), lng: Number(lng) };
      location = formattedCoords;

      var marker = new window.google.maps.Marker({
        position: location,
        map: window.map,
        icon: img
      })

      const contentString = 
        `<div>
          <h1>${name}</h1>
          <p>Payment Options: ${payOptions}</p>
          <p>Hours: ${hours}</p>
          <p>Popular Items: ${popItems}</p>
          <p>Est. Wait Time: ${waitTime}</p>
          <a href="/index.html">
            <button>Check Reviews</button>
          </a>
        </div>`;

      var infoWindow = new window.google.maps.InfoWindow({
        content: contentString
      });

      marker.addListener('click', function() {
        infoWindow.open(window.map, marker);
      });
    }
    try {
      restaurantData.forEach(restaurant =>{
        addMarker(
          restaurant.name,
          restaurant.location,
          restaurant.payOptions,
          restaurant.hours,
          restaurant.popItems,
          restaurant.waitTime,
          restaurant.img
        )
      })
    }
    catch (error) {
      console.log(error);
    }
  }, [restaurantData, loaded]);

  if (userInfo == null || restaurantData == null) {
    console.log("NOTHING LOADED YET");
    return <div>Loading...</div>;
  }
  else {
    setLoaded(true);
  }

  return (
    <div>
      <title>My Google Maps</title>
      <div className="well" id="studentInfo">
        <div id="header">Your Info</div>
        <div>CaseCash: {userInfo.caseCash}</div>
        <div>Portable Swipes: {userInfo.portSwipes}</div>
        <div>Meal Swipes: {userInfo.mealSwipes}</div> 
        <div>Review Points: {userInfo.reviewPoints}</div>
      </div>
    </div>
  );
}

export default Map;
